    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        Cliente cliente1=new Cliente();
        cliente1.getNombre(jTextField1.getText());
        cliente1.getApellido(jTextField2.getText());
        cliente1.getDni(jTextField3.getText());
        cliente1.getFecha(jTextField4.getText());
        cliente1.getTelefono(jTextField5.getText());
        cliente1.getEmail(jTextField6.getText());
        
        cliente1.muestra_objeto();

        Ventana2 f = new Ventana2(cliente1);
        f.setVisible(true);
        dispose();

    }//GEN-LAST:event_jButton1ActionPerformed

